class Room:
    def __init__(self, description, items=None, exits=None):
        self.description = description
        self.items = items if items else []
        self.exits = exits if exits else {}

    def has_item(self, item):
        return item in self.items

    def remove_item(self, item):
        self.items.remove(item)

    def add_item(self, item):
        self.items.append(item)


class Player:
    def __init__(self, starting_room):
        self.inventory = []
        self.current_room = starting_room

    def take_item(self, item):
        if item in self.current_room.items:
            self.inventory.append(item)
            self.current_room.remove_item(item)
            print(f"You picked up the {item}.")
        else:
            print(f"There's no {item} here.")

    def use_item(self, item):
        if item in self.inventory:
            if item == 'key':
                if self.current_room.description == "Exit Room":
                    print("You use the key to unlock the door. You escape!")
                else:
                    print("You can't use the key here.")
            else:
                print(f"You can't use the {item} here.")
        else:
            print("You don't have that item.")

    def move(self, direction):
        next_room = self.current_room.exits.get(direction)
        if next_room:
            self.current_room = next_room
            print(f"You go {direction}.")
        else:
            print("You can't go that way.")


class Game:
    def __init__(self):
        self.starting_room = Room("You are in a small room. There's a note on the wall.", items=['note'])
        self.hallway = Room("You are in a narrow hallway. The walls are damp.", items=[])
        self.library = Room("You are in a dusty library. Shelves line the walls.", items=['key'])
        self.kitchen = Room("You are in a kitchen. The smell of garlic fills the air.", items=[])
        self.exit_room = Room("You are in an exit room. There's a door to the north.", items=[])

        self.starting_room.exits = {'north': self.hallway}
        self.hallway.exits = {'south': self.starting_room, 'east': self.library}
        self.library.exits = {'west': self.hallway, 'south': self.kitchen}
        self.kitchen.exits = {'north': self.library, 'east': self.exit_room}
        self.exit_room.exits = {'west': self.kitchen}

        self.player = Player(self.starting_room)

    def start(self):
        print("Welcome to the adventure game!")
        print(self.player.current_room.description)
        while True:
            command = input("What do you want to do? ").strip().lower()
            if command == 'quit':
                print("Thanks for playing!")
                break
            elif command == 'look':
                print(self.player.current_room.description)
                if self.player.current_room.items:
                    print("You see:", ', '.join(self.player.current_room.items))
            elif command.startswith('go '):
                direction = command.split(' ')[1]
                self.player.move(direction)
            elif command.startswith('take '):
                item = command.split(' ')[1]
                self.player.take_item(item)
            elif command.startswith('use '):
                item = command.split(' ')[1]
                self.player.use_item(item)
            elif command == 'inventory':
                if self.player.inventory:
                    print("Inventory:", ', '.join(self.player.inventory))
                else:
                    print("Your inventory is empty.")
            else:
                print("Invalid command. Try again.")
            if self.player.current_room == self.exit_room and 'key' in self.player.inventory:
                print("Congratulations! You escaped!")
                break


if __name__ == '__main__':
    game = Game()
    game.start()
